import {
  SET_CURRENT_USER,
} from "./types";// Register User


export const loginUser = (userData, history) => dispatch => {
  fetch('https://api.app.lettria.com/auth/login', {
            method: 'POST',
            withCredentials: true,
            headers: {
                'Content-type': 'application/json',
                'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhbmFseXRpY0lkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU0IiwicHJvamVjdElkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU1Iiwic3Vic2NyaXB0aW9uSWQiOiI1ZjZkZjFkYmY5YWVjMjE2MjM3N2RjNGUiLCJpYXQiOjE2MDE0MDAwMjgsImV4cCI6MTY0OTc4NDAyOH0.rUV5ChRNY3iKviougXmCLuvrbexYwy1fXxy4O-Bm7_Y',
              },
             body: JSON.stringify({
                email: userData.email,
                password: userData.password,
						})
        })
        .then(results => results.json())
				.then(res => {
          if(res.user){
            dispatch(setCurrentUser(res));
              history.push({pathname:"/user/profil"})
            }
        })
};

export const updateInfoProfil = (userData) => dispatch => {
  fetch('https://api.app.lettria.com/auth/modifyUser', {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
                'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhbmFseXRpY0lkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU0IiwicHJvamVjdElkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU1Iiwic3Vic2NyaXB0aW9uSWQiOiI1ZjZkZjFkYmY5YWVjMjE2MjM3N2RjNGUiLCJpYXQiOjE2MDE0MDAwMjgsImV4cCI6MTY0OTc4NDAyOH0.rUV5ChRNY3iKviougXmCLuvrbexYwy1fXxy4O-Bm7_Y',
              },
             body: JSON.stringify({
                firstName: userData.firstName,
                lastName: userData.lastName,
                email: "chacha.bouricheau@yop.com"
						})
        })
        .then(results => results.json())
				.then(res => {
          dispatch(setCurrentUser(res));
        })
};


export const setCurrentUser = res => {
  return {
    type: SET_CURRENT_USER,
    user: res.user,
    isAuthenticated: true
  };
};// User loading
