import React, { Component } from "react";
import store from '../store/store';
import { connect } from "react-redux";
import { loginUser } from "../actions/authActions";

class UpdateProfil extends Component {
  constructor() {
		super();
	 	this.state = {
			first_name: store.getState().auth.user.user.profile.firstName,
	 		last_name: store.getState().auth.user.user.profile.last_name,
	 		email: store.getState().auth.user.user.profile.email,
		}
		console.log(store.getState().auth.user.user.profile)
		this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {



	}

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();
    const userData = {
      email: this.state.email,
			username: this.state.username,
			first_name: this.state.first_name,
			last_name:this.state.last_name,
		};

		this.props.updateInfoProfil(userData,this.props.history);
		 // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
  };

  render() {
    return (
      <div>
				<div className="container wave-box-wrapper">
				<div className="wave-box" />
				<form noValidate onSubmit={this.onSubmit}>
					<div className="col-md-10 col-md-offset-1">
						<div className="panel panel-default">
							<div className="panel-heading">My Account</div>
							<div className="panel-body">
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Email<span className="text-red">:</span></label>
									<div className="col-md-6">
										<input type="text" id="email" value={this.state.email}  onChange={this.onChange} placeholder={this.state.email} disabled className="form-control" />
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">First Name:</label>
									<div className="col-md-6">
										<input type="first_name" id="first_name"  onChange={this.onChange} value={this.state.first_name} placeholder={this.state.first_name} className="form-control" />
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Last Name:</label>
									<div className="col-md-6">
										<input type="last_name" id="last_name" onChange={this.onChange} value={this.state.last_name} placeholder={this.state.last_name} className="form-control" />
									</div>
								</div>		
							</div>
						</div>
					</div>
				</form>
			</div>
		</div> 
    )
  }
}

function mapStateToProps(state){
	return {
		first_name:state.auth.user.user.profile.firstName
	}
}

export default connect(
	mapStateToProps,
	{loginUser}
)(UpdateProfil);
