import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import { withRouter } from 'react-router-dom';

class App extends Component {

    constructor() {
        super();
        this.state = {
            data: [],
            email: "",
            password: "",
            token: "",
        };
    } //end constructor

    componentWillMount() {
    }

    componentDidMount() {
        this.fetchData();
    }

    fetchData() {
        fetch('https://api.app.lettria.com/auth/login', {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
                'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhbmFseXRpY0lkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU0IiwicHJvamVjdElkIjoiNWY2ZGYyMDY4MzU1M2ExNjI5NzBkMWU1Iiwic3Vic2NyaXB0aW9uSWQiOiI1ZjZkZjFkYmY5YWVjMjE2MjM3N2RjNGUiLCJpYXQiOjE2MDE0MDAwMjgsImV4cCI6MTY0OTc4NDAyOH0.rUV5ChRNY3iKviougXmCLuvrbexYwy1fXxy4O-Bm7_Y',

            },
             body: JSON.stringify({
                email: 'myUserName',
                password: 'myPassword',
            })
        }) /*end fetch */
        .then(results => results.json())
        .then(data => this.setState({ data: data })
        )
    }

    //request the token
      requestAccessToken(data) {
        const loginInfo = '${data}&grant_type=password';
        return fetch('${API_URL}Token', {
          method: 'POST',
          headers: new Headers({
            'Content-Type': 'application/json',
          }),
          body: loginInfo,
        })
          .then((response) => response.json());
      }

      //authenticate request
      requestUserInfo(token) {
        return fetch('${API_URL}api/participant/userinfo', {
          method: 'GET',
          headers: new Headers({
            Authorization: 'Bearer ${token}',
          }),
        })
          .then((response) => response.json());
      }

    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }; //end change

    onSubmit = (e) =>{
        this.fetchData();
        e.preventDefault();
        console.log(this.state);
        this.setState({
             email: "",
             password: "",
            });

        this.props.history.push('/landing');
        };

    render() {
    console.log(this.state.data);
        return (
           <div>
                <div className="loginContainer">
                <h2>Member Login</h2>
                    <form>
                            <input
                            id="username"
                            name="username"
                            placeholder="User Name"
                            value={this.state.username}
                            onChange={e => this.change(e) }
                            className="form-control"
                            />  <br />

                            <input
                            id="password"
                            name="password"
                            type="password"
                            placeholder="Password"
                            value={this.state.password}
                            onChange={e => this.change(e) }
                            className="form-control"
                            />  <br />

                        <button onClick={e => this.onSubmit(e)} className="btn btn-primary">Submit</button>
                        </form>
                    </div>
            </div>
        );
      }
}

export default withRouter(App);
