import React, { Component } from "react";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import classnames from "classnames";
import { loginUser } from "../actions/authActions";

class UpdateProfil extends Component {
  constructor() {
    super();
    this.state = {
			images:"",
			email: "",
			old_password:"",
			password: "",
			password2: "",
			username: "",
			first_name: "",
			last_name:"",
			phone_number:"",
			about:"",
			isActive:true,
      errors: {},
		};
		this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {

    // If logged in and user navigates to Login page, should redirect them to dashboard
	}

  componentWillReceiveProps(nextProps) {

    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
		console.log(e.target.value)
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();
    const userData = {
      email: this.state.email,
			password: this.state.password,
			old_password:this.state.old_password,
			password2: this.state.password2,
			username: this.state.username,
			first_name: this.state.first_name,
			last_name:this.state.last_name,
			phone_number:this.state.phone_number,
			about:this.state.about,
		};

		this.props.updateInfoProfil(userData,this.props.history);
		 // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
  };

  render() {
	    const { errors } = this.state;

    return (
      <div>
				<Alert message={this.props.location.message} status={this.props.location.status}/>
				<div className="container wave-box-wrapper">
				<div className="wave-box" />
				<form noValidate onSubmit={this.onSubmit}>
					<div className="col-md-10 col-md-offset-1">
						<p className="ml-b"><a href="/user/profil" className="ml-b text-success"><span className="glyphicon glyphicon-chevron-left" /> Back to profil</a>
						</p><div className="panel panel-default">
							<div className="panel-heading">My Account</div>
							<div className="panel-body">
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Email<span className="text-red">:</span></label>
									<div className="col-md-6">
										<input type="text" id="email" value={this.state.email}  onChange={this.onChange} placeholder={this.state.email} disabled className="form-control" />
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Old password:</label>
									<div className="col-md-6">
										<input type="password" id="old_password" value={this.state.old_password}  onChange={this.onChange} className={classnames("form-control", {
                    invalid: errors.old_password
                  })}/>
									<span className="red-text">{errors.old_password}</span>
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">New password:</label>
									<div className="col-md-6">
										<input type="password" id="password" value={this.state.password}  onChange={this.onChange} className={classnames("form-control", {
                    invalid: errors.password
                  })}/>
									<span className="red-text">{errors.password}</span>
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Confirm New password:</label>
									<div className="col-md-6">
										<input type="password" id="password2" value={this.state.password2}  onChange={this.onChange} className={classnames("form-control", {
                    invalid: errors.password2
                  })}/>
									<span className="red-text">{errors.password2}</span>
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Username:</label>
									<div className="col-md-6">
										<input type="username" id="username" value={this.state.username}  onChange={this.onChange} placeholder={this.state.username} disabled className="form-control" />
									</div>
								</div>
								<div className="form-group clearfix">
									<label className="col-md-4 control-label text-right">Profil picture:</label>
									<div className="col-md-6">
										<section id="upload">
											<img className="uploaded-image" alt="" style={{maxWidth: '200px', maxHeight: '200px'}} src={this.state.images}/>
